    public static class TryDCLStableValue implements StableValue {
        private static final VarHandle HANDLE;

        public final Supplier factory;

        private T cache;

        public TryDCLStableValue(Supplier factory) {
            this.factory = factory;
        }


        @Override
        public T get() {
            T res = (T) HANDLE.get(this);
            if (res != null) {
                return res;
            }

            synchronized (this) {
                res = (T) HANDLE.getVolatile(this);
                if (res != null) {
                    return res;
                }
                res = factory.get();
                HANDLE.setVolatile(this, res);
                return res;
            }
        }

        static {
            try {
                HANDLE = MethodHandles.lookup().findVarHandle(TryDCLStableValue.class, "cache", Object.class);
            } catch (NoSuchFieldException | IllegalAccessException e) {
                throw new RuntimeException(e);
            }
        }
    }

