public static class DCLStableValue implements StableValue {
    public final Supplier factory;

    private volatile T cache;

    public DCLStableValue(Supplier factory) {
        this.factory = factory;
    }


    @Override
    public T get() {
        if (cache != null) {
            return cache;
        }

        synchronized (this) {
            if (cache != null) {
                return cache;
            }
            return cache = factory.get();
        }
    }
}

